def get_data():
    with open("hip.txt","r") as file:
        data2 = eval(file.readline())
    with open("thigh.txt","r") as file:
        data3 = eval(file.readline())
    with open("calf.txt","r") as file:
        data4 = eval(file.readline())
    data2 = np.array(data2)
    data3 = np.array(data3)
    data4 = np.array(data4)
    A = np.vstack((data2,data3))
    result = np.vstack((A,data4))

    return result

